import {useState, useRef} from 'react'
import Todos from './Todos'
export default function App() {
  const [todos, setTodos] = useState(<></>)
  const name = useRef(null)
  const task = useRef(null)
  function makeObject(...args){
    const result = []
    args.map(arg=>{
      result.push({})
    })
  }
  return (
    <>
      <div class="todos">
        <div class="input">
          <div class="input__inputs">
            <input onChange={e=>name.current = e.currentTarget.value} maxlength="25" type="text" class="input__inputs__name" placeholder="Название" />
            <textarea onChange={e=>task.current = e.currentTarget.value} maxlength="230" class="input__inputs__task" placeholder="Задание"></textarea>
          </div>
          <button class="input__create"><img class="input__create__img" src="./add.png"/></button>
        </div>
      </div>
      <Todos />
    </>);
}
