import {useRef} from 'react'
export default function Todos() {
    // const todos = localStorage.getItem(localStorage.key(0))
    const todos = []
    for(let i=0; i<localStorage.length; i++){
        let key = localStorage.key(i)
        let content = localStorage.getItem(key).split(key)
        todos.push({key: key, name: content[0], task: content[1]})
    }
    console.log(todos)
    let render = []
    let keys = useRef([])
    todos.map(el=>keys.current.push(el.key))
    todos.map((el, index)=>{
        render.push(
            <>
                <li className="todos" key={keys[index]}>
                    <div className="listElement">
                        <div className="listElement__elements">
                            <h1 className="listElement__elements__name">{el.name}</h1>
                            <p className="listElement__elements__task">{el.task}</p>
                        </div>
                    </div>
                </li>
            </>
        )
    })
    return(<ul>{render}</ul>)
}
