import {useState, useRef} from 'react'
export default function App() {
  const [todos, setTodos] = useState(null)
  const name = useRef(null)
  const task = useRef(null)
  return (
    <>
      <div class="todos">
        <div class="input">
          <div class="input__inputs">
            <input onChange={e=>name.current = e.currentTarget.value} maxlength="25" type="text" class="input__inputs__name" placeholder="Название" />
            <textarea onChange={e=>tas.current = e.currentTarget.value} maxlength="230" class="input__inputs__task" placeholder="Задание"></textarea>
          </div>
          <button class="input__create"><img class="input__create__img" src="./add.png"/></button>
        </div>
      </div>
    </>
  , todos);
}
